// SidebarView.component.js
import React from 'react';
import './SidebarView.styles.css';

const SidebarView = ({ emails, activeEmailId, onEmailClick }) => {
  return (
    <div className="sidebar-container">
      <h1 className="heading">Email Sidebar View</h1>
      {emails.map((email) => (
        <div
          key={email.id}
          className={`email-item ${email.id === activeEmailId ? 'active' : ''} ${email.read ? 'read' : 'unread'}`}
          onClick={() => onEmailClick(email.id)}
        >
          <div className="email-info">
            <div className="email-from">{email.from}</div>
            <div className="email-subject">{email.subject}</div>
            <div className="email-address">{email.address}</div>
            <div className="email-timestamp">{email.time}</div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default SidebarView;
