// SidebarView.component.jsx

import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar/SearchBar.component';
import EmailList from './emaillist.component'; 

const SidebarView = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const response = await fetch('https://gist.githubusercontent.com/15e1989583fd6e6e04e1c49287934c91/raw/ed03cfea1e2edb0303543d2908cd7429ed75580d/email.json');
        const data = await response.json();
        setEmails(data);
      } catch (error) {
        console.error('Error fetching emails:', error);
      }
    };

    fetchEmails();
  }, []);

  return (
    <div className="sidebar-container">
      <h1 className="heading">Email Sidebar View</h1>
      <SearchBar placeholder="Search Bar" handleInput={(e) => setSearchTerm(e.target.value)} />
      <EmailList emails={emails} />
    </div>
  );
};

export default SidebarView;
