import React, { useState, useEffect } from 'react';
import './App.css';
import SidebarView from './SidebarView/SidebarView.component';
import BodyView from './BodyView/BodyView.component';


function App() {
  const [selectedEmail, setSelectedEmail] = useState(null);

  return (
    <div className="app-container">
      <SidebarView setSelectedEmail={setSelectedEmail} />
      <BodyView selectedEmail={selectedEmail} />
    </div>
  );
}

export default App;
