import React, { useState, useEffect } from 'react';
import './App.css';
import SidebarView from './SidebarView/SidebarView.component';
import BodyView from './BodyView/BodyView.component';


const App = () => {
  const [emails, setEmails] = useState([]);
  const [activeEmailId, setActiveEmailId] = useState(null);

  useEffect(() => {
    // Fetch email data from the provided URL
    fetch('https://gist.githubusercontent.com/15e1989583fd6e6e04e1c49287934c91/raw/ed03cfea1e2edb0303543d2908cd7429ed75580d/email.json')
      .then(response => response.json())
      .then(data => setEmails(data.map(email => ({ ...email, status: email.read ? 'read' : 'unread' }))))
      .catch(error => console.error('Error fetching email data:', error));
  }, []);

  const handleEmailClick = (emailId) => {
    setActiveEmailId(emailId);
    setEmails(prevEmails =>
      prevEmails.map(email =>
        email.id === emailId ? { ...email, read: true } : email
      )
    );
  };

  const activeEmail = emails.find((email) => email.id === activeEmailId);

  return (
    <div className="app-container">
      <SidebarView emails={emails} activeEmailId={activeEmailId} onEmailClick={handleEmailClick} />
      <BodyView activeEmail={activeEmail} />
    </div>
  );
};

export default App;
