// SidebarView.component.jsx

import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar/SearchBar.component';
import EmailCard from './email/email.component';
import './SidebarView.styles.css';

const SidebarView = () => {
  const [emails, setEmails] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedEmail, setSelectedEmail] = useState(null);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const response = await fetch('https://gist.githubusercontent.com/mrchenliang/15e1989583fd6e6e04e1c49287934c91/raw/ed03cfea1e2edb0303543d2908cd7429ed75580d/email.json');
        const data = await response.json();
        setEmails(data);
      } catch (error) {
        console.error('Error fetching emails:', error);
      }
    };

    fetchEmails();
  }, []);

  // Filter emails based on search term
  const filteredEmails = emails.filter((email) =>
    email.from.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleEmailClick = (email) => {
    setSelectedEmail(email);
  };

  return (
    <div className="sidebar-container">
      <h1 className="heading">Email Sidebar View</h1>
      <SearchBar placeholder="Search Bar" handleInput={(e) => setSearchTerm(e.target.value)} />
      <div className="email-list">
        {filteredEmails.map((email) => (
          <EmailCard
            key={email.id}
            email={{
              id: email.id,
              from: email.from,
              address: email.address,
              time: email.time,
              subject: email.subject
            }}
            onClick={() => handleEmailClick(email)}
          />
        ))}
      </div>
    </div>
  );
};

export default SidebarView;
