// SidebarView.component.jsx

import React from 'react';
import SearchBar from './SearchBar/SearchBar.component';
import EmailCard from './email/email.component';
import './SidebarView.styles.css';

const SidebarView = () => {
  // Manually create an array of sample emails
  const sampleEmails = [
    {
      "id": "1",
      "from": "Sample User",
      "address": "chen@omnipresent.com",
      "time": "2021-10-07 15:35:14",
      "message": "Duis cursus, diam at pretium aliquet, metus urna convallis erat, eget tincidunt dui augue eu tellus. Phasellus elit pede, malesuada vel, venenatis vel, faucibus id, libero. Donec consectetuer mauris id sapien. Cras",
      "subject": "Omnipresent Interview",
      "tag": "inbox",
      "read": "false"
    },
    {
      "id": "2",
      "from": "Dribbble",
      "address": "digest@dribbble.com",
      "time": "2021-05-09 14:23:54",
      "message": "Here are the latest shots from Dribbblers you follow! Nec mauris blandit mattis. Cras eget nisi dictum augue malesuada malesuada. Integer id magna et ipsum cursus vestibulum. Mauris magna. Duis dignissim tempor arcu. Vestibulum ut eros non enim commodo hendrerit. Donec porttitor tellus non magna. Nam ligula elit, pretium et, rutrum non, hendrerit id, ante. Nunc mauris sapien,",
      "subject": "Dribbble Digest",
      "tag": "inbox",
      "read": "false"
    }
  ];

  // Render the sample emails
  return (
    <div className="sidebar-container">
      <h1 className="heading">Email Sidebar View</h1>
      {/* Omitting the SearchBar for simplicity */}
      <div className="email-list">
        {sampleEmails.map((email) => (
          <EmailCard key={email.id} email={email} />
        ))}
      </div>
    </div>
  );
};

export default SidebarView;
