// BodyView.component.js
import React from 'react';
import './bodyview.styles.css';

const BodyView = ({ activeEmail }) => {
  return (
    <div className="body-container">
      <h1 className="heading">Email Body View</h1>
      {activeEmail ? (
        <div className="email-body">
          <div className="email-content">
            <div className="email-from">{activeEmail.from}</div>
            <div className="email-subject">{activeEmail.subject}</div>
            <div className="email-timestamp">{activeEmail.timestamp}</div>
          </div>
          <div className="email-message">{activeEmail.message}</div>
        </div>
      ) : (
        <p className="no-email-selected">No email selected</p>
      )}
    </div>
  );
};

export default BodyView;
