import React, { useState } from 'react';
import SearchBar from './SearchBar/SearchBar.component';
import LeftMargin from './LeftMargin/LeftMargin.component'; 
import './SidebarView.styles.css';

const SidebarView = ({ emails, activeEmailId, onEmailClick }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredEmails = emails.filter((email) =>
    email.from.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="sidebar-container">
      <LeftMargin /> {/* Include the LeftMargin component here */}
      <div className="content-container">
        <h1 className="heading">Email Sidebar View</h1>
        <SearchBar placeholder="Search Bar" handleInput={(e) => setSearchTerm(e.target.value)} />
        {filteredEmails.map((email) => (
          <div
            key={email.id}
            className={`email-item ${email.id === activeEmailId ? 'active' : ''} ${email.read ? 'read' : 'unread'}`}
            onClick={() => onEmailClick(email.id)}
          >
            <div className="email-info">
              <div className="email-from">{email.from}</div>
              <div className="email-subject">{email.subject}</div>
              <div className="email-address">{email.address}</div>
              <div className="email-timestamp">{email.time}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SidebarView;
